	public function buildCriteria()
	{
		$criteria = new Criteria(AccidentePeer::DATABASE_NAME);

		if ($this->isColumnModified(AccidentePeer::ID)) $criteria->add(AccidentePeer::ID, $this->id);
		if ($this->isColumnModified(AccidentePeer::IDCHOFER)) $criteria->add(AccidentePeer::IDCHOFER, $this->idchofer);
		if ($this->isColumnModified(AccidentePeer::IDMOVIL)) $criteria->add(AccidentePeer::IDMOVIL, $this->idmovil);
		if ($this->isColumnModified(AccidentePeer::FECHA)) $criteria->add(AccidentePeer::FECHA, $this->fecha);
		if ($this->isColumnModified(AccidentePeer::RESPONSABLE)) $criteria->add(AccidentePeer::RESPONSABLE, $this->responsable);
		if ($this->isColumnModified(AccidentePeer::ESQUINA)) $criteria->add(AccidentePeer::ESQUINA, $this->esquina);
		if ($this->isColumnModified(AccidentePeer::HERIDOS)) $criteria->add(AccidentePeer::HERIDOS, $this->heridos);
		if ($this->isColumnModified(AccidentePeer::DEDUCIBLE)) $criteria->add(AccidentePeer::DEDUCIBLE, $this->deducible);
		if ($this->isColumnModified(AccidentePeer::DESCRIPCION)) $criteria->add(AccidentePeer::DESCRIPCION, $this->descripcion);
		if ($this->isColumnModified(AccidentePeer::FECHAALTA)) $criteria->add(AccidentePeer::FECHAALTA, $this->fechaalta);
		if ($this->isColumnModified(AccidentePeer::FECHABAJA)) $criteria->add(AccidentePeer::FECHABAJA, $this->fechabaja);
		if ($this->isColumnModified(AccidentePeer::HABILITADO)) $criteria->add(AccidentePeer::HABILITADO, $this->habilitado);
		if ($this->isColumnModified(AccidentePeer::USUARIO)) $criteria->add(AccidentePeer::USUARIO, $this->usuario);

		return $criteria;
	}

