		$criteria = clone $criteria;

		// Set the correct dbName if it has not been overridden
		// $criteria->getDbName() will return the same object if not set to another value
		// so == check is okay and faster
		if ($criteria->getDbName() == Propel::getDefaultDB()) {
			$criteria->setDbName(self::DATABASE_NAME);
		}

		PagoaseguradoraPeer::addSelectColumns($criteria);
		$startcol2 = (PagoaseguradoraPeer::NUM_COLUMNS - PagoaseguradoraPeer::NUM_LAZY_LOAD_COLUMNS);

		EmpresaPeer::addSelectColumns($criteria);
		$startcol3 = $startcol2 + (EmpresaPeer::NUM_COLUMNS - EmpresaPeer::NUM_LAZY_LOAD_COLUMNS);

		AseguradoraPeer::addSelectColumns($criteria);
		$startcol4 = $startcol3 + (AseguradoraPeer::NUM_COLUMNS - AseguradoraPeer::NUM_LAZY_LOAD_COLUMNS);

		MovilPeer::addSelectColumns($criteria);
		$startcol5 = $startcol4 + (MovilPeer::NUM_COLUMNS - MovilPeer::NUM_LAZY_LOAD_COLUMNS);

		$criteria->addJoin(PagoaseguradoraPeer::IDEMPRESA, EmpresaPeer::ID, $join_behavior);

		$criteria->addJoin(PagoaseguradoraPeer::IDASEGURADORA, AseguradoraPeer::ID, $join_behavior);

		$criteria->addJoin(PagoaseguradoraPeer::IDMOVIL, MovilPeer::ID, $join_behavior);

		// symfony_behaviors behavior
		foreach (sfMixer::getCallables(self::getMixerPreSelectHook(__FUNCTION__)) as $sf_hook)
		{
		  call_user_func($sf_hook, 'BasePagoaseguradoraPeer', $criteria, $con);
		}


		$stmt = BasePeer::doSelect($criteria, $con);
		$results = array();

		while ($row = $stmt->fetch(PDO::FETCH_NUM)) {
			$key1 = PagoaseguradoraPeer::getPrimaryKeyHashFromRow($row, 0);
			if (null !== ($obj1 = PagoaseguradoraPeer::getInstanceFromPool($key1))) {
				// We no longer rehydrate the object, since this can cause data loss.
				// See http://propel.phpdb.org/trac/ticket/509
				// $obj1->hydrate($row, 0, true); // rehydrate
			} else {
				$cls = PagoaseguradoraPeer::getOMClass(false);

				$obj1 = new $cls();
				$obj1->hydrate($row);
				PagoaseguradoraPeer::addInstanceToPool($obj1, $key1);
			} // if obj1 already loaded

				// Add objects for joined Empresa rows

				$key2 = EmpresaPeer::getPrimaryKeyHashFromRow($row, $startcol2);
				if ($key2 !== null) {
					$obj2 = EmpresaPeer::getInstanceFromPool($key2);
					if (!$obj2) {
	
						$cls = EmpresaPeer::getOMClass(false);

					$obj2 = new $cls();
					$obj2->hydrate($row, $startcol2);
					EmpresaPeer::addInstanceToPool($obj2, $key2);
				} // if $obj2 already loaded

				// Add the $obj1 (Pagoaseguradora) to the collection in $obj2 (Empresa)
				$obj2->addPagoaseguradora($obj1);

			} // if joined row is not null

				// Add objects for joined Aseguradora rows

				$key3 = AseguradoraPeer::getPrimaryKeyHashFromRow($row, $startcol3);
				if ($key3 !== null) {
					$obj3 = AseguradoraPeer::getInstanceFromPool($key3);
					if (!$obj3) {
	
						$cls = AseguradoraPeer::getOMClass(false);

					$obj3 = new $cls();
					$obj3->hydrate($row, $startcol3);
					AseguradoraPeer::addInstanceToPool($obj3, $key3);
				} // if $obj3 already loaded

				// Add the $obj1 (Pagoaseguradora) to the collection in $obj3 (Aseguradora)
				$obj3->addPagoaseguradora($obj1);

			} // if joined row is not null

				// Add objects for joined Movil rows

				$key4 = MovilPeer::getPrimaryKeyHashFromRow($row, $startcol4);
				if ($key4 !== null) {
					$obj4 = MovilPeer::getInstanceFromPool($key4);
					if (!$obj4) {
	
						$cls = MovilPeer::getOMClass(false);

					$obj4 = new $cls();
					$obj4->hydrate($row, $startcol4);
					MovilPeer::addInstanceToPool($obj4, $key4);
				} // if $obj4 already loaded

				// Add the $obj1 (Pagoaseguradora) to the collection in $obj4 (Movil)
				$obj4->addPagoaseguradora($obj1);

			} // if joined row is not null

			$results[] = $obj1;
		}
		$stmt->closeCursor();
		return $results;
	}

