	/**
	 * Retrieve object using using composite pkey values.
	 * @param      int $idempresa
	 * @param      int $idaseguradora
	 * @param      int $idmovil
	 * @param      string $pagocorrespondiente
	 * @param      PropelPDO $con
	 * @return     Pagoaseguradora
	 */
	public static function retrieveByPK($idempresa, $idaseguradora, $idmovil, $pagocorrespondiente, PropelPDO $con = null) {
		$key = serialize(array((string) $idempresa, (string) $idaseguradora, (string) $idmovil, (string) $pagocorrespondiente));
 		if (null !== ($obj = PagoaseguradoraPeer::getInstanceFromPool($key))) {
 			return $obj;
		}

		if ($con === null) {
			$con = Propel::getConnection(PagoaseguradoraPeer::DATABASE_NAME, Propel::CONNECTION_READ);
		}
		$criteria = new Criteria(PagoaseguradoraPeer::DATABASE_NAME);
		$criteria->add(PagoaseguradoraPeer::IDEMPRESA, $idempresa);
		$criteria->add(PagoaseguradoraPeer::IDASEGURADORA, $idaseguradora);
		$criteria->add(PagoaseguradoraPeer::IDMOVIL, $idmovil);
		$criteria->add(PagoaseguradoraPeer::PAGOCORRESPONDIENTE, $pagocorrespondiente);
		$v = PagoaseguradoraPeer::doSelect($criteria, $con);

		return !empty($v) ? $v[0] : null;
	}
